import { useEffect, useState } from "react"
import { View, Text, StyleSheet, FlatList, Image, TouchableOpacity } from "react-native"
import * as ImagePicker from "expo-image-picker"
import { useNavigation } from "@react-navigation/native"

const Tarefas = () => {
    const navigation = useNavigation()
    const goToTarefa = () => {
        navigation.navigate('Tarefa' as never)
    }
    const [tarefa, setTarefa] = useState([
        {
            "id_tarefa": 1,
            "nome_tarefa": "Levar para passeio",
            "conclusao": "pendente",
            "data_tarefa": "12/11/2024",
            "hora": "17:00",
            "id_animal": 1
        },
        {
            "id_tarefa": 2,
            "nome_tarefa": "Levar para tosa",
            "conclusao": "concluido",
            "data_tarefa": "12/11/2024",
            "hora": "17:00",
            "id_animal": 1
        },
        {
            "id_tarefa": 3,
            "nome_tarefa": "Levar para veterinário",
            "conclusao": "pendente",
            "data_tarefa": "12/11/2024",
            "hora": "17:00",
            "id_animal": 1
        },
        {
            "id_tarefa": 4,
            "nome_tarefa": "Levar para veterinário",
            "conclusao": "pendente",
            "data_tarefa": "12/11/2024",
            "hora": "17:00",
            "id_animal": 1
        },
        {
            "id_tarefa": 5,
            "nome_tarefa": "Levar para veterinário",
            "conclusao": "pendente",
            "data_tarefa": "12/11/2024",
            "hora": "17:00",
            "id_animal": 1
        }
    ])
    const [pet, setPet] = useState(
        {
            id_animal: 1,
            nome_animal: 'pet',
            raca_animal: 'dog',
            bio: 'Lorem ipsum dolor, sit amet consectetur adipisicing elit. Cumque suscipit enim dolorem, voluptas asperiores quod officia quo accusantium.',
            foto_animal: ''
        }
    )
    const [img, setImg] = useState('https://via.placeholder.com/130x130')
    useEffect(
        () => {
            setImg(pet.foto_animal)
        }
    )
    const [newImg, setNewImg] = useState('')
    const handleImagePicker = async () => {
        const result = await ImagePicker.launchImageLibraryAsync({
            aspect: [4,4],
            allowsEditing: true,
            base64: true,
            quality: 1,
        })
        if (!result.canceled) {
            setNewImg(result.assets[0].uri)
        }
    }
    return (
        <View style={styles.container}>
            <View style={styles.flex}>
                <View style={styles.petinfo}>
                    <TouchableOpacity onPress={handleImagePicker}>
                        {
                            newImg ? <Image source={{uri: newImg}} style={styles.image}/> :
                            <Image source={{uri: `data:image/png;base64,${img}`}} style={styles.image}/>
                        }
                    </TouchableOpacity>
                    <View>
                        <Text style={styles.nome}>Nome: {pet.nome_animal}</Text>
                        <Text style={styles.raca}>Raça: {pet.raca_animal}</Text>
                    </View>
                </View>
            </View>
            <Text>{pet.bio}</Text>
            <View style={styles.flex}>
                <Text style={styles.title}>Confira a agenda de seu pet</Text>
                <Image source={require('../../../../assets/arrow.png')} style={[styles.icon, { transform: [{ scaleX: -1 }] }]} />
            </View>
            <FlatList 
                data={tarefa}
                keyExtractor={(item) => item.id_tarefa.toString()}
                renderItem={({ item }) => (
                <TouchableOpacity onPress={goToTarefa}>
                    <View style={styles.tarefa}>
                    <View style={styles.info}>
                        <View>
                            <Text style={styles.name}>{item.nome_tarefa}</Text>
                            <Text>{item.data_tarefa}</Text>
                            <Text>{item.hora}</Text>
                            {
                                item.conclusao === "pendente"? <Text style={styles.pendente}>{item.conclusao}</Text> :
                                <Text style={styles.concluido}>{item.conclusao}</Text>
                            }
                        </View>
                    </View>
                </View>
                </TouchableOpacity>
                )}
                contentContainerStyle={styles.listContainer}
            />
        </View>
    )
}

const styles = StyleSheet.create({
    title: {
        fontSize: 22,
        paddingTop: 32,
        paddingBottom: 32,
    },
    image: {
        width: 130,
        height: 130,
        marginBottom: 16,
        marginRight: 16,
        borderRadius: 16,
    },
    tarefa: {
        flexDirection: 'row',
    },
    info: {
        flex: 1,
        flexDirection: 'row',
        marginBottom: 24,
        alignItems: 'flex-start',
        justifyContent: 'space-between'
    },
    name: {
        fontSize: 20
    },
    nome: {
        fontSize: 28
    },
    raca: {
        fontSize: 24
    },
    bio: {
        color: '#6e6e6e',
    },
    icon: {
        height: 25,
        width: 25,
        marginLeft: 16,
    },
    flex: {
        flexDirection: 'row',
        alignItems: 'center'
    },
    petinfo: {
        flex: 1,
        flexDirection: 'row'
    },
    pendente: {
        width: 100,
        textAlign: 'center',
        backgroundColor: 'yellow',
        padding: 8,
        borderRadius: 16
    },
    concluido: {
        width: 100,
        textAlign: 'center',
        backgroundColor: 'green',
        padding: 8,
        borderRadius: 16
    },
    excluir: {
        textAlign: 'center',
        backgroundColor: 'red',
        padding: 8,
        borderRadius: 16,
        color: '#fff'
    },
    listContainer: {
        paddingBottom: 20,
    },
    container: {
        flex: 1,
        paddingLeft: 32,
        paddingRight: 32,
        backgroundColor: '#fff'
    }
});

export default Tarefas
